import { assertValidVersion } from "@tsparticles/engine";
import { InteractivityParticleMaker } from "./InteractivityParticleMaker.js";
export async function loadExternalParticleInteraction(engine, refresh = true) {
    assertValidVersion(engine, "3.7.1");
    await engine.addInteractor("externalParticle", container => {
        return Promise.resolve(new InteractivityParticleMaker(container));
    }, refresh);
}
