import { EasingType, assertValidVersion } from "@tsparticles/engine";
export async function loadEasingLinearPlugin(engine, refresh = true) {
    assertValidVersion(engine, "3.7.1");
    await engine.addEasing(EasingType.easeInLinear, value => {
        return value;
    }, false);
    await engine.addEasing(EasingType.easeOutLinear, value => {
        return value;
    }, false);
    await engine.addEasing(EasingType.easeInOutLinear, value => {
        return value;
    }, false);
    await engine.refresh(refresh);
}
