import { assertValidVersion } from "@tsparticles/engine";
import { OrbitUpdater } from "./OrbitUpdater.js";
export async function loadOrbitUpdater(engine, refresh = true) {
    assertValidVersion(engine, "3.7.1");
    await engine.addParticleUpdater("orbit", container => {
        return Promise.resolve(new OrbitUpdater(container, engine));
    }, refresh);
}
